import './Draggable.css';
import {useState, useEffect} from 'react'

function Draggable(props) {

  const [pos, setPos] = useState({
    x: 0,
    y: 0
  })

  const [postState, setPostState] = useState(false) 
  // const [mousePosition, setMousePosition] = useState({ x: null, y: null });
  const [deltaPos, setDeltaPos] = useState({
    x: null,
    y: null,
  })

  const updateMousePosition = ev => {
    // console.log(deltaPos.x)
    if(postState)  {
      setPos({
        x: ev.pageX-deltaPos.x,
        y: ev.pageY-deltaPos.y   
      }) 

    }



  };

  useEffect(() => {
    window.addEventListener("mousemove", updateMousePosition);

    return () => window.removeEventListener("mousemove", updateMousePosition);
  }, []);


  const onMouseDown = (e) => {
    console.log('down')
    setPostState(true)
    e.preventDefault();
    setDeltaPos({
      x: e.pageX,
      y: e.pageY
    })

  }

  const onMouseUp = (e) => {
      // console.log('onMouseUp')
      // setPostState(false)

  }

  const onMouseMove = (e) => {
      console.log(postState)
      setPos({
        x: e.pageX,
        y: e.pageY   
      })    

  }  

  return (
    <div onMouseMove={postState ? onMouseMove : null} onMouseUp={onMouseUp} className={postState ? 'wrap' : null}>
  <span  onMouseDown={onMouseDown} onMouseUp={onMouseUp} style={{transform: 'translate('+pos.x+'px,'+pos.y+'px'+')'}}  className={postState ? 'element active' : 'element'}>{props.children}</span>       
    </div>
  );
}

export default Draggable;
