import React, {useState, useRef} from 'react'
import Tree from './Containers/Tree/Tree'
import {useSelector, useDispatch} from "react-redux"
import {openAndCloseNode, addNode, deleteNode} from './Actions/index' 
import ContentEditable from 'react-contenteditable'


const Layout = () => {
    const tree = useSelector(state => state.tree)
    const dispatch = useDispatch();
    const text = useRef('');

    const handleChange = evt => {
        text.current = evt.target.value;
    };

    const handleBlur = () => {
        console.log(text.current);
    };

    const openAndCloseNodeFun = (id) => {
        dispatch(openAndCloseNode(id));
        // console.log(id)
    }

    const addNodeFun = (id) => {
        dispatch(addNode(id));
    }

    const deleteNodeFun = (id) => {
        dispatch(deleteNode(id))
    }

    return (

        <>
            <ContentEditable html={text.current} onBlur={handleBlur} onChange={handleChange} />
            <Tree openNode={openAndCloseNodeFun} addNode={addNodeFun} items={tree} deleteNodeFun={deleteNodeFun}/>
        </>
    )
}

export default Layout
