const R = require('ramda')




/////////////////////////////////// that function return a path wraped in array to primitive of object which was passed

const findPath = (primitive, object, path = []) => {
    for (const [key, value] of Object.entries(object)) {
      if (value === primitive) {
        return [...path, key];
      }

      if (typeof value === 'object' || typeof value === 'function') {
        const newPath = findPath(primitive, value, [...path, key]);
        if (newPath) {
          return newPath;
        }
      }
    }
    return null;
  }

const composeLense = (updatedPath) => {
    return R.compose(...updatedPath.map(item => isNaN(item) ? R.lensProp(item) : R.lensIndex(parseInt(item))));
}

///////////////////////////////////// that function return updated state of tree

const openAndClose = (id, state) => {

    const updatedPath = [...findPath(id,state).slice(0, findPath(id,state).length-1), 'state']
    const boolState = !R.view(composeLense(updatedPath), state);
    const newState = R.set(composeLense, boolState, state);

    return newState;
}

