import React, {useEffect, useState} from 'react'
import './Tree.css';
import ContentEditable from 'react-contenteditable'
import Circle from '../Circle/Circle'
import Draggable from 'react-draggable';

const TreeItem = ({item, openNode, addNode, deleteNodeFun, editTitle, dispatchF}) => {


    const [typing, setTyping] = useState({
        state: false,
        timeCode: null,
        val: item.title
    })

    const [isDragging, setIsDragging] = useState(false);
    const [lastNode, setLastNode] = useState(null)

    useEffect(() => {
        return () => {
            clearTimeout(typing.timeCode)
        }
    })

    const dispatchFun = () => {
        dispatchF(lastNode)
    }

    const onMouseOver = (id) => {
        setLastNode(id)
        // console.log(id) 

    }

    const isDraggingFun = (val) => {
        // console.log(val)
        setIsDragging(val)
    }

    function handleChange(evt) {

        if(typing.state === true){
            clearTimeout(typing.timeCode)
        }

        const timer = setTimeout(() => {
            setTyping({
                state: false,
                timeCode: null,
                val: evt.target.value
            })
            editTitle(item.id, evt.target.value)
        }, 2000);

        setTyping({
            state: true,
            timeCode: timer,
            val: evt.target.value
        })

    };

    return (
        <div className="tree_node" style={{paddingLeft: '20px', cursor: 'pointer'}}>
            <Circle isDragging={isDraggingFun} dispatchFun={dispatchFun}/>
            <div className="tree_node_item" onMouseOver={() => onMouseOver(item.title)}>
                <ContentEditable className='tree_node_item_add-button' html={typing.state ? typing.val : item.title}  onChange={handleChange} />
                <h5 className='tree_node_item_add-button' onClick={() => openNode(item.id)}>^</h5>
                <h5 className='tree_node_item_add-button' onClick={() => addNode(item.id)}>add</h5>
                <h5 className='tree_node_item_add-button' onClick={() => typing.state ? null : deleteNodeFun(item.id)}>del</h5>
            </div>
            {item.items && item.state === true ? <Tree items={item.items} dispatchF={dispatchF} editTitle={editTitle} openNode={openNode} addNode={addNode} deleteNodeFun={deleteNodeFun}/> : null}
        </div>
    )
} 



const Tree = ({items, openNode, addNode, deleteNodeFun, editTitle, dispatchF}) => <div>
    {items.map((i, key) => <TreeItem  key={key} item={i} dispatchF={dispatchF} openNode={openNode} addNode={addNode} editTitle={editTitle} deleteNodeFun={deleteNodeFun}/>)}    
</div>


export default Tree;
