import React, {useState} from 'react'
import MyNavbar from './DumbComponents/Navbar/MyNavbar'
import Sidebar from './DumbComponents/Sidebar/Sidebar'
import Tasks from './DumbComponents/TaskSection/Tasks/Tasks'
import { useSelector } from 'react-redux'
import {useDispatch} from 'react-redux';
import {complete, addTask, deleteTask, priorTask, catAdd} from './Actions/index';
import {
    Switch,
    Route
} from "react-router-dom";
import queryString from 'query-string'

const Layout = ({location}) => {

    const tasks = useSelector(state => state.tasks);
    const categories = useSelector(state => state.categories);
    const dispatch = useDispatch();
    const params = queryString.parse(location.search)

    const com = (id) => {
        // console.log(params)
        dispatch(complete(id))
    }

    const taskAdd = (obj) => {
        dispatch(addTask(obj))
    }

    const del = (id) => {
        dispatch(deleteTask(id));
    }

    const prior = (id, priority) => {
        dispatch(priorTask(id, priority))
    }

    const categoryAdd = (cat) => {
        console.log(categories)
        dispatch(catAdd(cat))
    }


    // console.log(tasks);

    return (
        <div>
            <MyNavbar/>            
            <Sidebar categAdd={categoryAdd} cats={categories}/>
            <Switch>

                {/* {categories.map((item,i) => ( */}
                    <Route  path={'/'}>
                        <Tasks location={location}  prior={prior} del={del} taskAdd={taskAdd} complete={com} tasks={tasks}/>
                    </Route>
                {/* ))} */}


            </Switch>

        </div>
    )
}

export default Layout
