import React, {useState} from 'react'
import MyNavbar from './DumbComponents/Navbar/MyNavbar'
import Sidebar from './DumbComponents/Sidebar/Sidebar'
import Tasks from './DumbComponents/TaskSection/Tasks/Tasks'
import { useSelector } from 'react-redux'
import {useDispatch} from 'react-redux';
import {complete, addTask, deleteTask, priorTask, catAdd} from './Actions/index';
import {
    Switch,
    Route
} from "react-router-dom";

const Layout = () => {

    const tasks = useSelector(state => state.tasks);
    const categories = useSelector(state => state.categories);
    const dispatch = useDispatch();
    const [currentCategory, setCurrentCategory] = useState('');

    const com = (id) => {
        dispatch(complete(id))
    }

    const taskAdd = (obj) => {
        dispatch(addTask(obj))
    }

    const del = (id) => {
        dispatch(deleteTask(id));
    }

    const prior = (id, priority) => {
        dispatch(priorTask(id, priority))
    }

    const categoryAdd = (cat) => {
        // console.log(cat)
        dispatch(catAdd(cat))
    }

    const setCat = (val) => {
        setCurrentCategory(val)
        console.log(val)
    }
    // console.log(tasks);

    return (
        <div>
            <MyNavbar/>
            <Sidebar setCat={setCat} catAdd={categoryAdd} cats={categories}/>
            <Switch>

                <Route path={'/' + currentCategory}>
                    <Tasks loc={currentCategory} prior={prior} del={del} taskAdd={taskAdd} complete={com} tasks={tasks}/>
                </Route>

            </Switch>

        </div>
    )
}

export default Layout
