import cat from './categories';

const tasks = (state =  [
    {
        id: '1605810959227',
        value: 'Milk',
        priority: 'low',
        complete: false
    },
    {
        id: '1605810977955',
        value: 'Bread',
        priority: 'high',
        complete: false
    },
    {
        id: '1605811001826',
        value: 'Butter',
        priority: 'mid',
        complete: false
    }
], action) => {
    switch(action.type){
        case 'COMPLETE':
            console.log(cat.val);
            return state.map(item => item.id === action.id ? { ...item, complete: !item.complete} : item);

        case 'ADD_TASK':

            return [...state, {
                id: Date.now(),
                value: action.obj.val,
                priority: 'mid',
                complete: false
            }];

        case 'DELETE_TASK':

            const index = {};
            state.map((item,i) => item.id === action.id ? index.id = i : '')
            return [...state.slice(0, index.id), ...state.slice(index.id + 1)]

        case 'PRIOR_TASK':

            return state.map(item => item.id === action.id ? { ...item, priority: action.prior} : item);

    }
    return state;   
}

export default tasks;
