const tasks = (state =  [
    {
        id: '1605810959227',
        value: 'Milk',
        priority: 'low',
        complete: false
    },
    {
        id: '1605810977955',
        value: 'Bread',
        priority: 'high',
        complete: false
    },
    {
        id: '1605811001826',
        value: 'Butter',
        priority: 'mid',
        complete: false
    }
], action) => {
    // console.log(action);
    switch(action.type){
        case 'COMPLETE':
            // console.log(action);
            for(let i = 0; i <= state.length; i++){
                if(state[i].id === action.id){
                    state[i].complete = !state[i].complete;
                }
            }

            return state;
    }
    return state;   
}

export default tasks;
