    for (const [key, value] of Object.entries(object)) {
      if (value === primitive) {
        return [...path, key];
      }

      if (typeof value === 'object' || typeof value === 'function') {
        const newPath = findPath(primitive, value, [...path, key]);
        if (newPath) {
          return newPath;
        }
      }
    }
    return null;
  }




function updateTree (prim, obj){
    const arr = [...findPath(prim,obj).slice(0, findPath(prim,obj).length-1), 'state']
