                  items: []
              }
          ]
      }
    ]


    const render = (tree, state=[]) => {
      let arr = state;
      tree.map((item) => {
          arr = [...arr, item.title] 
          console.log(item.title, arr);

          if(item.items !== []){
              render(item.items, arr);
          }          
      } )
      // return state;  
      // console.log(tree)
    }

    const renderTree = (tree, state=[1]) => {
        let route = state;
        if(tree.route === route[route.length-1]){
          route.push(route[route.length-1]*10+1)
          console.log(tree.route, route)
          renderTree(tree.route, route)
        } else if(tree.route === route[route.length-1]+1){
          route[route.length-1]=route[route.length-1]+1;
          console.log(tree.route, route)
          renderTree(tree.route, route);
        } else if(route.length !== 0) {
          route.pop();
          console.log(tree.route, route)
          renderTree(tree.route, route);
        } else {
          return state;
        }
    }

    console.log(renderTree(tree))
    // console.log(render(tree))

  return (
    <div>
    </div>
  )
}

