const tasks = (state =  [
    {
        id: '1605810959227',
        value: 'Milk',
        priority: 'low',
        complete: false
    },
    {
        id: '1605810977955',
        value: 'Bread',
        priority: 'high',
        complete: false
    },
    {
        id: '1605811001826',
        value: 'Butter',
        priority: 'mid',
        complete: false
    }
], action) => {
    switch(action.type){
        case 'COMPLETE':

            return state.map(item => item.id === action.id ? { ...item, complete: !item.complete} : item);
        case 'ADD_TASK':


            return [...state, {
                id: Date.now(),
                value: action.obj.val,
                priority: 'low',
                complete: false
            }];

    }
    return state;   
}

export default tasks;
