import React, {useEffect, useState} from 'react'
import './Tree.css';
import ContentEditable from 'react-contenteditable'




const TreeItem = ({item, openNode, addNode, deleteNodeFun}) => {

    const [typing, setTyping] = useState({
        state: false,
        timeCode: null,
        val: item.title
    })

    function handleChange(evt) {


        const timer = setTimeout(() => {
            setTyping({
                state: false,
                timeCode: null,
                val: typing.val
            })
            console.log(evt.target.value)
        }, 1000);

        setTyping({
            state: true,
            timeCode: timer,
            val: evt.target.value
        })
        // await editT(evt.target.value)
        // console.log(evt.target.value);
    };

    return (
        <div className="tree_node" style={{paddingLeft: '20px', cursor: 'pointer'}}>
            <div className="tree_node_item">
                {/* <h4 onClick={() => openNode(item.id)}>{item.title}</h4> */}
                <ContentEditable className='tree_node_item_add-button' html={typing.val}  onChange={handleChange} />
                <h5 className='tree_node_item_add-button' onClick={() => openNode(item.id)}>^</h5>
                <h5 className='tree_node_item_add-button' onClick={() => addNode(item.id)}>add</h5>
                <h5 className='tree_node_item_add-button' onClick={() => deleteNodeFun(item.id)}>del</h5>
            </div>
            {item.items && item.state === true ? <Tree items={item.items} openNode={openNode} addNode={addNode} deleteNodeFun={deleteNodeFun}/> : null}
        </div>
    )
} 





const Tree = ({items, openNode, addNode, deleteNodeFun}) => <div>
    {items.map((i, key) => <TreeItem  key={key} item={i} openNode={openNode} addNode={addNode} deleteNodeFun={deleteNodeFun}/>)}    
</div>


export default Tree;
