const tasks = (state =  [
    {
        id: '1605810959227',
        value: 'Milk',
        priority: 'low',
        complete: false
    },
    {
        id: '1605810977955',
        value: 'Bread',
        priority: 'high',
        complete: false
    },
    {
        id: '1605811001826',
        value: 'Butter',
        priority: 'mid',
        complete: false
    }
], action) => {
    // console.log(action);
    switch(action.type){
        case 'COMPLETE':
            // console.log(action);
            // const state_item = state
            // console.log({ ...state[0], complete: 'yeah'});

            return state.map(item => item.id === action.id ? { ...item, completed: !item.completed} : item);
    }
    return state;   
}

export default tasks;
