import React from 'react'
import Task from '../Task/Task'
import Filter from '../Filter/Filter'
import Input from '../Input/Input'
import './Tasks.css';

const Tasks = (props) => {
    const tasks = props.tasks;
    // console.log(props.tasks)
    const compl = (id) => {
        // console.log(id)
        props.complete(id);
    }

    const taskAdd = (obj) => {
        props.taskAdd(obj)
    }

    return (
        <div className='tasks'>
            <Input taskAdd={taskAdd}/>
            {tasks.map((item, key) => <Task compl={compl} task={item} key={key}/>)}
            <Filter/>
        </div>
    )
}

export default Tasks
