const R = require('ramda')

const findNodeById = (tree, id, state=[]) => {
    let arr = state
    let currentIter;
    tree.map((item,i) => {
        // console.log(item.title);
        arr.push(...arr, i);
        currentIter = i;

        if(item.id === id){
            console.log(arr)
            return arr;
        }

        arr.push(...arr, 'items');


        if(item.items !== []){
            findNodeById(item.items, id, arr)
        }
    })
}   


function findPath(primitive, object, path = []) {
    for (const [key, value] of Object.entries(object)) {
      if (value === primitive) {
        return [...path, key];
      }
      if (typeof value === 'object' || typeof value === 'function') {
        const newPath = findPath(primitive, value, [...path, key]);
        if (newPath) {
          return newPath;
        }
      }
    }
    return null;
  }




const fun = () => {


    const dino = {
        name: 'Denver',
        type: 'dinosaur',
        friends: [
          {
            name: 'Wally',
            type: 'human',
            pets: [
              {
                name: 'Rocky',
                type: 'dog'
              }
            ]
          },
          {
            name: 'Casey',
            type: 'human'
          }
        ]
      }
    // dino is in scope

    // making some lenses
    const friendsLens = R.lensProp('friends')
    const lens0 = R.lensIndex(0)
    const petsLens = R.lensProp('pets')
    const nameLens = R.lensProp('name')

    // stacking lenses together into a single lens
    const frns0pets0name = R.compose(
    friendsLens,  // outermost lens first
    lens0,
    petsLens,
    lens0,
    nameLens      // innermost lens last
    )

    // viewing data using a lens
    console.log(R.view(frns0pets0name, dino))

    // immutably setting data using a lens
    console.log(R.set(frns0pets0name, 'Spot', dino))

    // immutably mapping data using a lens
    console.log(R.over(frns0pets0name, (s => s + '!'), dino))

    console.log(dino) // unchanged


}


