import React from 'react'
import './Task.css'
import {useDispatch} from 'react-redux';
import {complete} from '../../../Actions/index'

const Task = (props) => {

    const task = props.task;
    const dispatch = useDispatch()

    const handler = (id) => {
        console.log('here');
        dispatch(complete(id))
    } 

    return (
        <div className="task">
            {/* <div className='task_check-mark'></div> */}
            <label className="container-3e4" >
                <input className="toggle" type="checkbox" checked={task.commplete ? 'checked' : ''} onChange={() => handler(task.id)} onClick={() => handler(task.id)}/>
                <span className="checkmark"/>
            </label>
            <div className='task_priority'>{task.priority}</div>
            <div className='task_content'>{task.value}</div>

        </div>
    )
}

export default Task
