const tasks = (state =  [
    {
        id: Date.now(),
        value: 'hello',
        priority: 'mid',
        complete: false,
        category: 'asd'
    }

], action) => {
    switch(action.type){

        case 'COMPLETE':
            // console.log(categories);
            return state.map(item => item.id === action.id ? { ...item, complete: !item.complete} : item);

        case 'ADD_TASK':

            return [...state, {
                id: Date.now(),
                value: action.obj.val,
                priority: 'mid',
                complete: false,
                category: action.obj.cat
            }];

        case 'DELETE_TASK':

            const index = {};
            state.map((item,i) => item.id === action.id ? index.id = i : '')
            return [...state.slice(0, index.id), ...state.slice(index.id + 1)]

        case 'PRIOR_TASK':

            return state.map(item => item.id === action.id ? { ...item, priority: action.prior} : item);


    }
    return state;   
}

export default tasks;
