import React from 'react'
import './Task.css'
import {complete} from '../../../Actions/index'

const Task = (props) => {

    const task = props.task;
    const dispatch = useDispatch()

    const handler = () => {
        dispatch(complete('1605811001826'))
        return 1;
    } 

    return (
        <div className="task">
            {/* <div className='task_check-mark'></div> */}
            <label className="container-3e4">
                <input className="toggle" type="checkbox" checked='checked' onChange={handler}/>
                <span className="checkmark"/>
            </label>
            <div className='task_priority'>{task.priority}</div>
            <div className='task_content'>{task.value}</div>

        </div>
    )
}

export default Task
