import PropTypes from 'prop-types';
import React from 'react'
import './Task.css'

const Task = (props) => {

    const task = props.task;

    const compl = (id) => {
        props.compl(id);
        // console.log(task);
    } 

    const del = (id) => {
        props.del(id)
    }

    const prior = (id, priority) => {
        console.log(priority)
        props.prior(id, priority)
    }

    return (
        <div className="task">
            {/* <div className='task_check-mark'></div> */}
            <label className="container-3e4" >
                <input className="toggle" type="checkbox" checked={task.complete ? 'checked' : ''} onChange={() => compl(task.id)}/>
                <span className="checkmark"/>
            </label>
            {/* <div className='task_priority'>{task.priority}</div> */}
            <div className="dropdown-r5t">
            <h3 className="current-priority">{task.priority}</h3>
            <div className='dropdown-content'>
                <div className="dropdown-content__high prior-element" onClick={()=>prior(task.id, 'high')}>high</div>
                <div className="dropdown-content__mid prior-element" onClick={()=>prior(task.id, 'mid')}>mid</div>
                <div className="dropdown-content__low prior-element" onClick={()=>prior(task.id, 'low')}>low</div>
            </div>            
            </div>
            <div className='task_content'>{task.value}</div>

            <div className="task_times" onClick={() => del(task.id)}>Ã—</div>

        </div>
    )
}

Task.propTypes = {
    compl: PropTypes.func,
    del: PropTypes.func,
    task: PropTypes.object
  };

export default Task
