import React from 'react'
import './Task.css'

const Task = (props) => {

    const task = props.task;

    const handler = (id) => {
        props.fun(id);
        console.log(task);
    } 

    return (
        <div className="task">
            {/* <div className='task_check-mark'></div> */}
            <label className="container-3e4" >
                <input className="toggle" type="checkbox" checked={task.complete ? 'checked' : ''} onChange={() => handler(task.id)}/>
                <span className="checkmark"/>
            </label>
            <div className='task_priority'>{task.priority}</div>
            <div className='task_content'>{task.value}</div>

        </div>
    )
}

export default Task
