import React from 'react'
import './Sidebar.css'
import Input from '../TaskSection/Input/Input'

const Sidebar = (props) => {

    const categories = props.cats;

    const catAdd = (cat) => {
        props.catAdd(cat)
        // console.log(categories)
    }

    const handler = (val) => {
        props.setCat(val);
    }

    return (
        <div className="sidebar">

            <Input taskAdd={catAdd}/>

            <h3 className="sidebar-title">Categories</h3>

                <ul className="sidebar-list">
                    {categories.map((item, i) => <li onClick={() => handler(item.val)} key={i}>{item.val}</li>)}

                </ul>

            <h3 className="sidebar-title">Filters</h3>

            <ul className="sidebar-list">
                    <li>
                        High priority
                    </li>
                    <li>
                        Mid priority
                    </li>
                    <li>
                        Low priority
                    </li>
                    <li>
                        All tasks
                    </li>
                </ul>

        </div>
    )
}

export default Sidebar
