import React, {useState} from 'react'
import Tree from './Containers/Tree/Tree'
import {useSelector, useDispatch} from "react-redux"
import {open, addNote} from './Actions/index' 



const Layout = () => {
    const tree = useSelector(state => state.tree)
    const dispatch = useDispatch();

    const openAndCloseNode = (id) => {
        dispatch(open(id));
        // console.log(id)
    }

    const addNoteFun = (id) => {
        dispatch(addNode(id));
    }

    return (

        <>

            <Tree openNode={openAndCloseNode} addNote={addNoteFun} items={tree}/>
        </>
    )
}

export default Layout
