import React from 'react'
import './Sidebar.css'
import Input from '../TaskSection/Input/Input'
import {useHistory} from 'react-router';
import PropTypes from 'prop-types';

const Sidebar = (props) => {

    const history = useHistory()

    const categories = props.cats;

    const catAdd = (cat) => {
        props.categAdd(cat)
        // console.log(categories)
    }

    const handler = (val) => {
        // props.setCat(val);
        history.push('?cat='+val);
    }

    return (
        <div className="sidebar">

            <Input taskAdd={catAdd}/>

            <h3 className="sidebar-title">Categories</h3>

                <ul className="sidebar-list">
                    {categories.map((item, i) => <li onClick={() => handler(item.val)} key={i}>{item.val}</li>)}

                </ul>

            <h3 className="sidebar-title">Filters</h3>

            <ul className="sidebar-list">
                    <li>
                        High priority
                    </li>
                    <li>
                        Mid priority
                    </li>
                    <li>
                        Low priority
                    </li>
                    <li>
                        All tasks
                    </li>
                </ul>

        </div>
    )
}

Sidebar.propTypes = {
    categAdd: PropTypes.func,
    cats: PropTypes.array
  };

export default Sidebar
