import React, {useEffect, useState} from 'react'
import './Tree.css';
import ContentEditable from 'react-contenteditable'
import Circle from '../Circle/Circle'
import Draggable from 'react-draggable';

const TreeItem = ({item, openNode, addNode, deleteNodeFun, editTitle, myLastNode, myDispatch}) => {


    const [typing, setTyping] = useState({
        state: false,
        timeCode: null,
        val: item.title
    })

    const [isDragging, setIsDragging] = useState(false);

    useEffect(() => {
        return () => {
            clearTimeout(typing.timeCode)
        }
    })



    const onMouseOver = (id) => {
        myLastNode(id)
        // console.log(id) 

    }

    const isDraggingFun = (val) => {
        // console.log(val)
        setIsDragging(val)
    }

    const myDispatchFun = () => {
        myDispatch()
    }

    const onDrop = (val) => {
        console.log(val)
    } 

    function handleChange(evt) {

        if(typing.state === true){
            clearTimeout(typing.timeCode)
        }

        const timer = setTimeout(() => {
            setTyping({
                state: false,
                timeCode: null,
                val: evt.target.value
            })
            editTitle(item.id, evt.target.value)
        }, 2000);

        setTyping({
            state: true,
            timeCode: timer,
            val: evt.target.value
        })

    };

    return (
        <div className="tree_node" style={{paddingLeft: '20px', cursor: 'pointer', paddingTop: '10px'}}>
            <Circle isDragging={isDraggingFun} myDispatch={myDispatchFun}/>
            <div className="tree_node_item" onDrop={() => onDrop(item.id)} onMouseUp={() => onDrop(item.id)} onMouseOver={() => onMouseOver(item.title)}>
                <ContentEditable className='tree_node_item_add-button' html={typing.state ? typing.val : item.title}  onChange={handleChange} />
                <h5 className='tree_node_item_add-button' onClick={() => openNode(item.id)}>^</h5>
                <h5 className='tree_node_item_add-button' onClick={() => addNode(item.id)}>add</h5>
                <h5 className='tree_node_item_add-button' onClick={() => typing.state ? null : deleteNodeFun(item.id)}>del</h5>
            </div>
            {item.items && item.state === true ? <Tree items={item.items} myLastNode={myLastNode}  myDispatch={myDispatch} editTitle={editTitle} openNode={openNode} addNode={addNode} deleteNodeFun={deleteNodeFun}/> : null}
        </div>
    )
} 



const Tree = ({items, openNode, addNode, deleteNodeFun, editTitle, myLastNode,myDispatch}) => <div>
    {items.map((i, key) => <TreeItem  key={key} item={i} myLastNode={myLastNode} openNode={openNode} myDispatch={myDispatch} addNode={addNode} editTitle={editTitle} deleteNodeFun={deleteNodeFun}/>)}    
</div>


export default Tree;
