import React from 'react'
import Task from '../Task/Task'
import Filter from '../Filter/Filter'
import Input from '../Input/Input'
import PropTypes from 'prop-types';
import './Tasks.css';


const Tasks = (props, location) => {
    const tasks = props.tasks.filter(item => item.category === props.cat);

    const compl = (id) => {
        props.complete(id);
    }

    const taskAdd = (obj) => {
        props.taskAdd(obj)
        console.log(tasks)
    }

    const del = (id) => {
        props.del(id);
    }

    const prior = (id, priority) => {
        props.prior(id, priority)
    }

    return (
        <div className='tasks'>
            <Input loc={props.cat} taskAdd={taskAdd}/>
            {tasks.map((item, key) => <Task del={del} prior={prior} compl={compl} task={item} key={key}/>)}
            <Filter/>
        </div>
    )
}

Tasks.propTypes = {
    tasks: PropTypes.array,
    taskAdd: PropTypes.func,
    compl: PropTypes.func,
    del: PropTypes.func
};

export default Tasks
