import React from 'react'
import './Input.css'

const Input = (props) => {

    function handleKeyPress(event) {
        if (event.key === "Enter" && inputEl.current.value !== "") {
            props.taskAdd({
                val:inputEl.current.value
            })
            inputEl.current.value = '';
        }
    }

    return (
        <div className='input-sec'>
            <input data-testid="txtNote" type="text" onKeyPress={handleKeyPress}
                className="input" placeholder="What needs to be done?" />
        </div>
    )
}

export default Input
