import React from 'react'
import MyNavbar from './DumbComponents/Navbar/MyNavbar'
import Sidebar from './DumbComponents/Sidebar/Sidebar'
import Tasks from './DumbComponents/TaskSection/Tasks/Tasks'
import { useSelector } from 'react-redux'
import {useDispatch} from 'react-redux';
import {complete, addTask, deleteTask, priorTask} from './Actions/index';


const Layout = () => {

    const tasks = useSelector(state => state.tasks);
    const dispatch = useDispatch()

    const com = (id) => {
        dispatch(complete(id))
    }

    const taskAdd = (obj) => {
        dispatch(addTask(obj))
    }

    const del = (id) => {
        dispatch(deleteTask(id));
    }

    const prior = (id, priority) => {
        dispatch(priorTask(id, priority))
    }

    // console.log(tasks);

    return (
        <div>
            <MyNavbar/>
            <Sidebar/>
            <Tasks prior={prior} del={del} taskAdd={taskAdd} complete={com} tasks={tasks}/>
        </div>
    )
}

export default Layout
