import './Draggable.css';
import {useState} from 'react'

function Draggable(props) {

  const [pos, setPos] = useState({
    x: 0,
    y: 0
  })

  const [postState, setPostState] = useState(false) 

  const [constPos, setConstPos] = useState({
    x: null,
    y: null
  })


  const onMouseDown = (e) => {
    // console.log(e)
    setPostState(true)
    e.preventDefault();

    setConstPos({
      x: e.pageX,
      y: e.pageY
    })

    setPos({
      x: 0,
      y: 0   
    })
  }

  const onMouseUp = (e) => {
    // console.log('onMouseUp')
    setPostState(false)
    setPos({
      x: 0,
      y: 0   
    })
  }

  const onMouseMove = (e) => {
    //   console.log(e)
    setPos({
      x: e.pageX-constPos.x-20,
      y: e.pageY-constPos.y-20   
    })    

  }  

  return (
    <div onMouseMove={postState ? onMouseMove : null} onMouseUp={onMouseUp}  onMouseLeave={onMouseUp} className={postState ? 'wrap' : null}>
      <span onMouseDown={onMouseDown}  onMouseUp={onMouseUp} style={{transform: 'translate('+pos.x+'px,'+pos.y+'px'+')'} }  className={postState ? 'element active' : 'element'}>{props.children}</span>       
    </div>
  );
}

export default Draggable;
